    var bignum = [];
    var i = 0;

    if (n < 0)
    {
        while (n < -bignum_radix_div2)
        {
            bignum[i++] = n & (bignum_radix-1);
            n = n >> bignum_radix_log2;
        } 
    }
    else
    {
        while (n >= bignum_radix_div2)
        {
            bignum[i++] = n & (bignum_radix-1);
            n = n >> bignum_radix_log2;
        } 
    }

